import{_ as e,c as o,o as t,a5 as a,a6 as i}from"./chunks/framework.tjZTTw8H.js";const m=JSON.parse('{"title":"Stiskanje tortilj","description":"","frontmatter":{"title":"Stiskanje tortilj"},"headers":[],"relativePath":"techniques/stiskanje-tortilj.md","filePath":"techniques/stiskanje-tortilj.md"}'),r={name:"techniques/stiskanje-tortilj.md"},s=a('<h1 id="stiskanje-tortilj" tabindex="-1">Stiskanje tortilj <a class="header-anchor" href="#stiskanje-tortilj" aria-label="Permalink to &quot;Stiskanje tortilj&quot;">​</a></h1><p>Tortilje je najlažje stiskati z <a href="https://en.wikipedia.org/wiki/Tortilla_press" target="_blank" rel="noreferrer">namensko napravo</a>, katere nakup pa ni smotrn, če jih ne pripravljaš dovolj pogosto. Spodnja tehnika predstavlja razumno alternativo, izvedljivo brez specializirane opreme.</p><ul><li>Najdi malce bolj robustno vrečko za živila (<a href="https://www.ikea.com/si/sl/p/istad-vrecka-z-zapiralom-vzorcasto-zivo-oranzna-30553669/" target="_blank" rel="noreferrer">nekaj takega</a>).</li><li>Prereži stranska robova na njej, da dobiš dolg trak.</li><li>Razgrni trak na pult, na en konec položi kroglico testa in drug konec prepogni čez.</li><li>Vzemi velik steklen pekač ali drugo težko posodo z ravnim dnom.</li><li>Z njegovo pomočjo splošči kroglico v tanko tortiljo.</li><li>Previdno jo odlepi izmed obeh slojev vrečke.</li></ul><video controls class="video"><source src="'+i+'" type="video/mp4"> Sorry, your browser doesn&#39;t support embedded videos. </video>',4),n=[s];function l(p,d,j,k,c,_){return t(),o("div",null,n)}const h=e(r,[["render",l]]);export{m as __pageData,h as default};
